#! /usr/bin/perl

use strict;

my $file = shift @ARGV;
my $sample = shift @ARGV;
my $out = "slout/seqs.fna";

open (F, $file) || die;
open (OUT, ">>$out") || die "Cannot open outfile";

my $count = 0;
while () {
  chomp $_;
  if ($_ =~ /^>/) {
    $count++;
    print OUT ">$sample" . "_" . "$count\n";
  }
  else {
    print OUT "$_\n";
  }
}
