source_data.name:
mkdir ./CARD_taxonomy_using_orfs/CARD_rpkm
for file in $(ls ./CARD_taxonomy_using_orfs/CARD_abundance_with_length); do
	export SUM=$(awk '{ SUM += $3 } END { print SUM }' ./CARD_taxonomy_using_orfs/CARD_abundance_with_length/${file})

echo Sum is $SUM for ${file}
	awk --assign sum=$SUM '{ print $1"\t"$2"\t"$3"\t"($3*1000000000/($2*sum)) }' ./CARD_taxonomy_using_orfs/CARD_abundance_with_length/${file} | sed "1 s/^/ORF_ID\tORF_Length\tHit_Count\t${file}\n/" > ./CARD_taxonomy_using_orfs/CARD_rpkm/${file}
done
