MAP_TP2 <- SUBSET_MAP[c(SUBSET_MAP$TimePoint==2),c(1,5:9)]
MAP_TP3 <- SUBSET_MAP[c(SUBSET_MAP$TimePoint==3),c(1,5:9)]
MAP_MERGE_REF <- merge(MAP_TP2, MAP_TP3, by=c("SubjectID","Site_Symbol","Site_Categories","Occlusion"))

SAMPLE_NAMES <- as.vector(MAP_MERGE_REF$NexteraXT_SampleID.x)

INTRAPERSONAL_DIST <- data.frame(lapply(SAMPLE_NAMES, function(i) {
  INTRAPERSON_DIST <- INPUT_SUBSET_DIST_MATRIX_MATRIX[c(row.names(INPUT_SUBSET_DIST_MATRIX_MATRIX)==i), as.vector(MAP_MERGE_REF[MAP_MERGE_REF$NexteraXT_SampleID.x==i,"NexteraXT_SampleID.y"])]
  SUBJECT <- MAP_MERGE_REF[MAP_MERGE_REF$NexteraXT_SampleID.x==i,"SubjectID"]
  SITE <- as.vector(MAP_MERGE_REF[MAP_MERGE_REF$NexteraXT_SampleID.x==i,"Site_Symbol"])
  RESULT <- data.frame(X=c(i, SUBJECT, SITE, INTRAPERSON_DIST))
  return(RESULT)
}))

INTERPERSONAL_DIST_TP3 <- data.frame(lapply(SAMPLE_NAMES, function(i) {
  INTERPERSON_DIST_TP3 <- INPUT_SUBSET_DIST_MATRIX_MATRIX[c(row.names(INPUT_SUBSET_DIST_MATRIX_MATRIX)==i), as.vector(MAP_MERGE_REF[-which(MAP_MERGE_REF$NexteraXT_SampleID.x %in% i),"NexteraXT_SampleID.y"])]
  SUBJECT <- MAP_MERGE_REF[MAP_MERGE_REF$NexteraXT_SampleID.x==i,"SubjectID"]
  SITE <- as.vector(MAP_MERGE_REF[MAP_MERGE_REF$NexteraXT_SampleID.x==i,"Site_Symbol"])
  TRANS <- data.frame(t(INTERPERSON_DIST_TP3))
  RESULT <- data.frame(X=c(SUBJECT, SITE, INTERPERSON_DIST_TP3))
  return(TRANS)
}))

INTERPERSONAL_DIST_TP2 <- data.frame(lapply(SAMPLE_NAMES, function(i) {
  INTERPERSON_DIST_T2 <- INPUT_SUBSET_DIST_MATRIX_MATRIX[c(row.names(INPUT_SUBSET_DIST_MATRIX_MATRIX)==i), as.vector(MAP_MERGE_REF[-which(MAP_MERGE_REF$NexteraXT_SampleID.x %in% i),"NexteraXT_SampleID.x"])]
  SUBJECT <- MAP_MERGE_REF[MAP_MERGE_REF$NexteraXT_SampleID.x==i,"SubjectID"]
  SITE <- as.vector(MAP_MERGE_REF[MAP_MERGE_REF$NexteraXT_SampleID.x==i,"Site_Symbol"])
  TRANS <- data.frame(t(INTERPERSON_DIST_T2))
  RESULT <- data.frame(X=c(SUBJECT, SITE, INTERPERSON_DIST_T2))
  return(TRANS)
}))
