promC <- do.call("rbind",lapplyBy(~PID,data=ShGeneWg,function(dx){
 if(dx$strand[1]=="+"){
 return(c(dx$contig[1],dx[dx$ExonN==1,"start"]-26,dx[dx$ExonN==1,"start"]+27,dx$PID[1],"1","+")) #returns the first codon 
  }
  if(dx$strand[1]=="-"){
    return(c(dx$contig[1],dx[dx$ExonN==1,"stop"]-28,dx[dx$ExonN==1,"stop"]+25,dx$PID[1],"1","-")) #returns the first codon
  }
}))
colnames(promC) <- c("chr","start","stop","name","frame","strand")
save(promC,file=paste(species,term,"promC","rda",sep="."))
