def correct_container(current_sample, original, overflow, capacity):
    """Given current sample number, will return the correct container if an overflow container exists."""
    if current_sample <= capacity:
        return original
    else:
        return overflow

def skip_row_four_well_position(current_sample, start_well=0):
    """
    Converts current sample number to corresponding well number on 
    96-well plate, if there were 4 samples per row plus a skip row 
    in between samples. Restarts when sample number is greater than 24.
    """
    if (current_sample + start_well) > 24:
        current_sample = current_sample - 24
    row = int(current_sample // 4.1)
    return (2 * current_sample - 1)  + (row * 8) + start_well

def four_position(current_sample, start=1):
    """
    Converts current sample number to string of the 
    corresponding row if there were 4 samples per row.
    """
    row = current_sample // 4 + start
    return 'A' + str(row)

def skip_row_position(current_sample, start=1):
    """
    Converts current sample number to a string of the corresponding row 
    if there were 8 samples per row, plus a skip row in between samples.
    """
    row = (current_sample // 8) * 2 + start
    return 'A' + str(row)

def qubit_sample_offset(current_sample, default):
    """
    Converts current sample number to a tuple that alternates 
    between an offset of 1 sample for the first half of the row, 
    or the default offset for the second half of the rows.
    """
    remainder = current_sample % 8
    if remainder <= 4:
        return (default[0] - 9, default[1], default[2])
    else:
        return default 

def skip_four_position(current_sample, start=1):
    """
    Converts current sample number to a string of the corresponding row 
    if there were 4 samples per row, plus a skip row in between samples.
    Restarts when there are greater than 12 rows.
    """
    row = 2 * (current_sample // 4) + start
    if row <= 12:
        return 'A' + str(row)
    else:
        return 'A' + str(row-12)
